function TombstoneConstructor() {
  const [page, setPage] = useState(0);
  const [showForm, setShowForm] = useState(true);
  const [showTombstone, setShowTombstone] = useState(false);
  const randomBlob = init();

  const transitionToFinal = () => {
    setShowForm(false);
    setTimeout(() => {
      setShowTombstone(true);
    }, 4000);
  };
  const FormTitles = [
    "this is the form of your <br /> tombstone",
    "choose<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;what best describes you<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;as a person",
    "what is important for you?"
  ];

  const CharacterDisplay = () => {
    if (page === 1) {
      return <Characters />
    } else if (page === 2) {
      return <Values />
    }
  };

  return(
    <div className="dark-screen">
      <CSSTransition
        in={showForm}
        timeout={3000}
        classNames="questions"
        unmountOnExit
        onExiting={() => setShowForm(false)}
      >
      {<div className="constructor-container">
        <div className="constructor-steps">
          <div className='progress-container'>
            <div className="dot dot-active"></div>
            <div className="dot" style={{opacity: page >= 1 ? '100%' : '10%'}}></div>
            <div className="dot" style={{opacity: page >= 2 ? '100%' : '10%'}}></div>
          </div>
          <h1 className="form-title"><Markup content={FormTitles[page]} /></h1>
          <div className="blob-container">
            <div className="constructor-field">
              <svg viewBox="0 0 1200 1200" fill="none" xmlns="http://www.w3.org/2000/svg" preserveAspectRatio="none">
                <path id="jagged-top" d={randomBlob} fill="black"/>
              </svg>
            </div>
            <h1 className="btn-constructor"
              onClick={() => {
                if (page === FormTitles.length - 1) {
                  transitionToFinal();
                } else {
                  setPage((currPage) => currPage + 1);
                }
              }}>
              next
            </h1>

          </div>
          {CharacterDisplay()}
          </div>
        </div>
    }
      </CSSTransition>
      <CSSTransition
        in={showTombstone}
        timeout={3000}
        classNames="constructor-3s"
        unmountOnExit
        onEnter={() => setShowForm(false)}
      >
      <FinalRyba />
      </CSSTransition>
    </div>
  );
}



